function convertSize(value: number, fromUnit: string, toUnit: string): number {
    const coefficients: Record<string, number> = {
      millimeter: 1,
      centimeter: 10,
      meter: 1000,
      inch: 25.4,
      foot: 304.8,
      yard: 914.4,
      mile: 1609344
    };
  
    const valueInMillimeters: number = value * coefficients[fromUnit];
    const convertedValue: number = valueInMillimeters / coefficients[toUnit];
  
    return convertedValue;
  }
  
  console.log(convertSize(10, 'inch', 'centimeter'));
  console.log(convertSize(5, 'meter', 'yard'));
  console.log(convertSize(2.5, 'mile', 'kilometer'));
  