class SizeConverter {
  private coefficients: Record<string, number>;

  constructor() {
    this.coefficients = {
      millimeter: 1,
      centimeter: 10,
      meter: 1000,
      inch: 25.4,
      foot: 304.8,
      yard: 914.4,
      mile: 1609344
    };
  }

  convertSize(value: number, fromUnit: string, toUnit: string): number {
    const valueInMillimeters: number = value * this.coefficients[fromUnit];
    const convertedValue: number = valueInMillimeters / this.coefficients[toUnit];

    return convertedValue;
  }
}
