class Measurement {
  length: number;
  width: number;
  height: number;
  unit: string;

  constructor(length: number, unit: string) {
    this.length = length;
    this.width = 0;
    this.height = 0;
    this.unit = unit;
  }

  convertToMeters(): void {
    switch (this.unit) {
      case 'mm':
        this.length /= 1000;
        break;
      case 'cm':
        this.length /= 100;
        break;
      case 'inch':
        this.length *= 0.0254;
        break;
      case 'foot':
        this.length *= 0.3048;
        break;
      case 'm':
        break;
      default:
        console.log('Invalid unit of measurement!');
    }
    this.unit = 'm';
  }

  convertToMillimeters(): void {
    switch (this.unit) {
      case 'm':
        this.length *= 1000;
        break;
      case 'cm':
        this.length *= 10;
        break;
      case 'inch':
        this.length *= 25.4;
        break;
      case 'foot':
        this.length *= 304.8;
        break;
      case 'mm':
        break;
      default:
        console.log('Invalid unit of measurement!');
    }
    this.unit = 'mm';
  }

  convertToCentimeters(): void {
    switch (this.unit) {
      case 'm':
        this.length *= 100;
        break;
      case 'mm':
        this.length /= 10;
        break;
      case 'inch':
        this.length *= 2.54;
        break;
      case 'foot':
        this.length *= 30.48;
        break;
      case 'cm':
        break;
      default:
        console.log('Invalid unit of measurement!');
    }
    this.unit = 'cm';
  }

  convertToInches(): void {
    switch (this.unit) {
      case 'm':
        this.length *= 39.3701;
        break;
      case 'mm':
        this.length /= 25.4;
        break;
      case 'cm':
        this.length /= 2.54;
        break;
      case 'foot':
        this.length *= 12;
        break;
      case 'inch':
        break;
      default:
        console.log('Invalid unit of measurement!');
    }
    this.unit = 'inch';
  }

  convertToFeet(): void {
    switch (this.unit) {
      case 'm':
        this.length *= 3.28084;
        break;
      case 'mm':
        this.length /= 304.8;
        break;
      case 'cm':
        this.length /= 30.48;
        break;
      case 'inch':
        this.length /= 12;
        break;
      case 'foot':
        break;
      default:
        console.log('Invalid unit of measurement!');
    }
    this.unit = 'foot';
  }
}

